import React, { useState } from 'react';

// Function to display stars based on the rating
const displayStars = (rating) => {
    let stars = '';
    for (let i = 0; i < 5; i++) {
        stars += i < rating ? '★' : '☆'; // Filled star or empty star
    }
    return stars;
};

const ReviewList = ({ reviews, averageRating }) => {
    const [showMore, setShowMore] = useState(false);
    const visibleReviews = showMore ? reviews : reviews.slice(0, 2); // Show only 2 reviews by default

    return (
        <div>
            <h3>Customer Reviews</h3>
            <div>
                <strong>Average Rating:</strong> {averageRating > 0 ? averageRating : '0'} / 5 {/* Display 0 if no reviews */}
            </div>
            {reviews.length === 0 ? (
                <p>No reviews yet.</p>
            ) : (
                visibleReviews.map((review, index) => (
                    <div 
                        key={index} 
                        style={{
                            border: '1px solid #ccc',
                            padding: '10px',
                            margin: '10px 0',
                            borderRadius: '5px',
                            backgroundColor: '#f9f9f9', // Light grey background color
                        }}
                    >
                        <p>{review.review}</p>
                        <small>{review.user.name}</small> {/* Ensure this accesses the correct user property */}
                        <p>Rating: {displayStars(review.rating)}</p> {/* Display stars instead of numerical rating */}
                    </div>
                ))
            )}
            {reviews.length > 2 && (
                <button onClick={() => setShowMore(!showMore)}>
                    {showMore ? 'Show Less Reviews' : 'More Reviews'}
                </button>
            )}
        </div>
    );
};

export default ReviewList;
