import React, { useState } from 'react';
import ChatBot from './ChatBot';

const ChatBotButton = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleChatBot = () => {
    setIsOpen(!isOpen);
  };

  return (
    <>
      {/* The chatbot window itself */}
      {isOpen && <ChatBot />}

      {/* The button that toggles the chatbot */}
      <div
        style={{
          position: 'fixed',
          bottom: '20px',
          right: '20px',
          width: '60px',
          height: '60px',
          backgroundColor: '#007bff',
          borderRadius: '50%',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          cursor: 'pointer',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
          zIndex: 1000,  // Ensure the button stays above other elements
        }}
        onClick={toggleChatBot}
      >
        <i style={{ fontSize: '24px', color: 'white' }}>ðŸ’¬</i> {/* Placeholder for chatbot icon */}
      </div>
    </>
  );
};

export default ChatBotButton;
