import React, { useState, useEffect } from 'react';
import ChatBot from './ChatBot';

const ChatBotButton = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [showAlert, setShowAlert] = useState(false);
  const [userName, setUserName] = useState('User'); // Default name

  // Simulating fetching user data
  useEffect(() => {
    // Replace this with actual user fetching logic
    const fetchUserData = async () => {
      // Simulate an API call to get user data
      const userData = await new Promise((resolve) => {
        setTimeout(() => resolve({ name: 'John Doe' }), 1000);
      });
      setUserName(userData.name);
    };

    fetchUserData();

    setShowAlert(true); // Show alert immediately after fetching user data
    const timer = setTimeout(() => {
      setShowAlert(false); // Hide alert after 5 seconds
    }, 5000); // Adjust duration as needed

    return () => clearTimeout(timer); // Cleanup timer on unmount
  }, []);

  const toggleChatBot = () => {
    setIsOpen(!isOpen);
  };

  return (
    <>
      {/* The alert bubble */}
      {showAlert && (
        <div style={{
          position: 'fixed',
          bottom: '90px', // Adjust position above the button
          right: '20px',
          backgroundColor: '#ffeb3b', // Yellow background for visibility
          padding: '10px',
          borderRadius: '5px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
          zIndex: 1001, // Ensure it stays above other elements
        }}>
          Hey {userName}! How can I help you?
        </div>
      )}

      {/* The chatbot window itself */}
      {isOpen && <ChatBot />}

      {/* The button that toggles the chatbot */}
      <div
        style={{
          position: 'fixed',
          bottom: '20px',
          right: '20px',
          width: '60px',
          height: '60px',
          backgroundColor: '#007bff',
          borderRadius: '50%',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          cursor: 'pointer',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
          zIndex: 1000,
        }}
        onClick={toggleChatBot}
      >
        <i style={{ fontSize: '24px', color: 'white' }}>ðŸ’¬</i>
      </div>
    </>
  );
};

export default ChatBotButton;
