import React, { useState } from 'react';
import { SliderData } from './SliderData';
import ImageSlider from './ImageSlider';
import Footer from './Footer';
import { Link } from 'react-router-dom';
import ChatBotButton from './ChatBotButton';

const Home = () => {
  const [position, setPosition] = useState({ x: window.innerWidth - 80, y: window.innerHeight - 80 });
  const [isDragging, setIsDragging] = useState(false);
  const [offset, setOffset] = useState({ x: 0, y: 0 });

  // Start dragging
  const handleMouseDown = (e) => {
    setIsDragging(true);
    setOffset({
      x: e.clientX - position.x,
      y: e.clientY - position.y
    });
  };

  // Dragging the button
  const handleMouseMove = (e) => {
    if (isDragging) {
      setPosition({
        x: e.clientX - offset.x,
        y: e.clientY - offset.y
      });
    }
  };

  // Stop dragging
  const handleMouseUp = () => {
    setIsDragging(false);
  };

  return (
    <>
      <ImageSlider slides={SliderData} />

      <div className="slider-text">
        <h1>RentTheTrend</h1>
        <h6>Clothes for all</h6>
        <Link to="/register" className="get-started-button">Get started â†’</Link>
      </div>

      <Footer />

      {/* Draggable Chatbot Button */}
      <div
        style={{
          position: 'fixed',
          top: `${position.y}px`,
          left: `${position.x}px`,
          zIndex: 10000,  // Ensure it appears on top
          cursor: isDragging ? 'grabbing' : 'grab',
          userSelect: 'none',
        }}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}  // Stop dragging if the mouse leaves the area
      >
        <div
          style={{
            backgroundColor: '#007bff',
            color: 'white',
            borderRadius: '50%',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
            width: '60px',
            height: '60px',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            fontSize: '20px',
          }}
        >
          <ChatBotButton />
        </div>
      </div>
    </>
  );
};

export default Home;
