import React, { useEffect, useState } from 'react';
import axios from 'axios';
import swal from 'sweetalert';
import { Link, useHistory } from 'react-router-dom';
import ReviewList from '../../ReviewList'; 
import ReviewForm from '../../ReviewForm'; 

function ProductDetail(props) {
  const history = useHistory();
  const [loading, setLoading] = useState(true);
  const [product, setProduct] = useState({});
  const [quantity, setQuantity] = useState(1);
  const [selectedSize, setSelectedSize] = useState('');
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    let isMounted = true;

    const fetchProductDetail = async () => {
      try {
        const category_slug = props.match.params.category;
        const product_slug = props.match.params.product;

        const productResponse = await axios.get(`/api/viewproductdetail/${category_slug}/${product_slug}`);
        if (isMounted) {
          if (productResponse.data.status === 200) {
            setProduct(productResponse.data.product);
            fetchRelatedProducts(productResponse.data.product.category_id);
            fetchReviews(productResponse.data.product.id);
            setLoading(false);
          } else if (productResponse.data.status === 404) {
            history.push('/collections');
            swal("Warning", productResponse.data.message, "error");
          }
        }
      } catch (error) {
        console.error("Error fetching product detail:", error);
        setLoading(false);
      }
    };

    fetchProductDetail();

    return () => {
      isMounted = false;
    };
  }, [props.match.params.category, props.match.params.product, history]);

  const fetchRelatedProducts = async (categoryId) => {
    try {
      const relatedProductsResponse = await axios.get(`/api/relatedproducts/${categoryId}`);
      if (relatedProductsResponse.data.status === 200) {
        setRelatedProducts(relatedProductsResponse.data.products);
      } else {
        console.error("Error fetching related products:", relatedProductsResponse.data.message);
      }
    } catch (error) {
      console.error("Error fetching related products:", error);
    }
  };

  const fetchReviews = async (productId) => {
    try {
      const reviewsResponse = await axios.get(`/api/products/${productId}/reviews`);
      setReviews(reviewsResponse.data.reviews);
    } catch (error) {
      console.error("Error fetching reviews:", error);
    }
  };

  const handleDecrement = () => {
    if (quantity > 1) {
      setQuantity(prevCount => prevCount - 1);
    }
  };

  const handleIncrement = () => {
    if (product.qty > quantity) {
      setQuantity(prevCount => prevCount + 1);
    }
  };

  const handleSizeChange = (size) => {
    setSelectedSize(size);
  };

  const submitAddToCart = async (e) => {
    e.preventDefault();

    try {
      const data = {
        product_id: product.id,
        product_qty: quantity,
        product_size: selectedSize,
      };

      const response = await axios.post(`/api/add-to-cart`, data);

      if (response.data.status === 201 || response.data.status === 409) {
        swal("Success", response.data.message, "success");
      } else if (response.data.status === 401 || response.data.status === 404) {
        swal("Error", response.data.message, "error");
      }
    } catch (error) {
      console.error("Error adding to cart:", error);
      swal("Error", "Failed to add to cart", "error");
    }
  };

  const handleReviewSubmitted = () => {
    fetchReviews(product.id);
  };

  if (loading) {
    return <h4>Loading Product Detail...</h4>;
  } else {
    const availStock = product.qty > 0 ? (
      <div>
        <label className="btn-sm btn-success px-4 mt-2">In stock - <span className='font-semibold text-lg '>{product.qty}</span></label>
      </div>
    ) : (
      <label className="btn-sm btn-danger px-4 mt-2">Out of Stock</label>
    );

    return (
      <div className="container">
        <div className="row py-5">
          <div className="col-md-6">
            <img src={`http://localhost:8000/${product.image}`} alt={product.name} className="img-fluid" />
          </div>
          <div className="col-md-6">
            <h2>{product.name}</h2>
            <h5>{product.brand}</h5>
            <h4>{product.price} {product.currency}</h4>
            {availStock}
            <div className="mt-4">
              <label>Size:</label>
              <div>
                {product.sizes && product.sizes.map((size, index) => (
                  <button
                    key={index}
                    className={`btn btn-sm ${selectedSize === size ? 'btn-primary' : 'btn-outline-primary'}`}
                    onClick={() => handleSizeChange(size)}
                  >
                    {size}
                  </button>
                ))}
              </div>
            </div>
            <div className="mt-4">
              <label>Quantity:</label>
              <div className="input-group">
                <button className="btn btn-outline-secondary" onClick={handleDecrement}>-</button>
                <input type="number" value={quantity} className="form-control" readOnly />
                <button className="btn btn-outline-secondary" onClick={handleIncrement}>+</button>
              </div>
            </div>
            <button className="btn btn-success mt-4" onClick={submitAddToCart}>Add to Cart</button>
          </div>
        </div>

        <div className="mt-5">
          <h4>Reviews</h4>
          <ReviewForm productId={product.id} onReviewSubmitted={handleReviewSubmitted} />
          <ReviewList reviews={reviews} />
        </div>

        <div className="mt-5">
          <h4>Related Products</h4>
          <div className="row">
            {relatedProducts.map((item, index) => (
              <div key={index} className="col-md-3">
                <div className="card">
                  <img src={`http://localhost:8000/${item.image}`} alt={item.name} className="card-img-top" />
                  <div className="card-body">
                    <h5 className="card-title">{item.name}</h5>
                    <p className="card-text">{item.price} {item.currency}</p>
                    <Link to={`/collections/${item.category_slug}/${item.slug}`} className="btn btn-primary">View Product</Link>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default ProductDetail;
