import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';

function ProductDetail() {
  const { category_slug, product_slug } = useParams();
  const history = useHistory();
  const [product, setProduct] = useState({});
  const [relatedProducts, setRelatedProducts] = useState([]);

  useEffect(() => {
    // Fetch product details
    axios.get(`/api/viewproductdetail/${category_slug}/${product_slug}`)
      .then(res => {
        if (res.data.status === 200) {
          const currentProduct = res.data.product;
          setProduct(currentProduct);

          // Fetch related products but exclude the current product
          fetchRelatedProducts(currentProduct.category_id, currentProduct.id);
        } else if (res.data.status === 404) {
          history.push('/collections');
        }
      });
  }, [category_slug, product_slug, history]);

  const fetchRelatedProducts = (categoryId, currentProductId) => {
    axios.get(`/api/relatedproducts/${categoryId}`)
      .then(res => {
        if (res.data.status === 200) {
          // Filter out the current product from the list of related products
          const filteredProducts = res.data.products.filter(product => product.id !== currentProductId);
          setRelatedProducts(filteredProducts);
        }
      });
  };

  return (
    <div className="container">
      <div className="product-detail">
        <h2>{product.name}</h2>
        <img src={`http://localhost:8000/${product.image}`} alt={product.name} />
        <p>{product.description}</p>
        <p>Price: Rs {product.selling_price}</p>
      </div>

      {/* Recommended Products Section */}
      <div className="recommended-products-section">
        <h2 className="section-title">Recommended for you</h2>
        <div className="row">
          {relatedProducts.length > 0 ? (
            relatedProducts.map((relatedProduct) => (
              <div key={relatedProduct.id} className="col-md-4">
                <div className="card h-100">
                  <img
                    src={`http://localhost:8000/${relatedProduct.image}`}
                    className="card-img-top"
                    alt={relatedProduct.name}
                    style={{ maxWidth: '100%', height: '150px' }}
                  />
                  <div className="card-body">
                    <h5 className="card-title">{relatedProduct.name}</h5>
                    <p className="card-text">Price: Rs {relatedProduct.selling_price}</p>

                    {/* Redirect to the selected product's detail page */}
                    <button
                      className="btn btn-primary"
                      onClick={() => history.push(`/collections/${relatedProduct.category.slug}/${relatedProduct.slug}`)}
                    >
                      View Product
                    </button>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <p>No recommended products available.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
