import React, { useState, useEffect } from 'react';
import axios from 'axios';
import StarRating from './StarRating'; 

const ReviewForm = ({ productId, onReviewSubmitted }) => {
    const [rating, setRating] = useState(0);
    const [review, setReview] = useState('');
    const [error, setError] = useState('');
    const [existingReviewId, setExistingReviewId] = useState(null);
    const [hasReviewed, setHasReviewed] = useState(false);

    useEffect(() => {
        if (productId) {
            checkExistingReview();
        } else {
            setError('Invalid product ID.');
        }
    }, [productId]);

    const getCurrentUser = () => ({
        id: localStorage.getItem('auth_id'), 
        name: localStorage.getItem('auth_name'),
    });

    const checkExistingReview = async () => {
        try {
            const response = await axios.get(`/api/products/${productId}/reviews`);
            const currentUser = getCurrentUser();

            if (currentUser.id) {
                const userReview = response.data.reviews.find(r => r.user.id === currentUser.id);
                if (userReview) {
                    setHasReviewed(true);
                    setExistingReviewId(userReview.id);
                    setRating(userReview.rating);
                    setReview(userReview.review);
                }
            }
        } catch (error) {
            console.error('Error checking existing review:', error);
            setError('Unable to fetch existing reviews.');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        if (rating < 1 || rating > 5) {
            setError('Please select a rating between 1 and 5.');
            return;
        }

        const reviewData = { productId, rating, review, date: new Date().toISOString() };

        try {
            const token = localStorage.getItem('auth_token');
            const url = hasReviewed && existingReviewId 
                ? `/api/reviews/${existingReviewId}` 
                : `/api/products/${productId}/reviews`;

            await axios({
                method: hasReviewed && existingReviewId ? 'put' : 'post',
                url,
                headers: { 'Authorization': `Bearer ${token}`, 'Content-Type': 'application/json' },
                data: reviewData,
            });

            onReviewSubmitted(reviewData); // Pass new review to parent
            setRating(0);
            setReview('');
            alert('Review submitted successfully!');

        } catch (err) {
            console.error('Error submitting review:', err);
            if (err.response && err.response.data.message) {
                setError(err.response.data.message);
            } else {
                setError('An error occurred while submitting your review.');
            }
        }
    };

    return (
        <div style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '5px', maxWidth: '400px' }}>
            <h3>Submit Your Review</h3>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Rating:</label>
                    <StarRating rating={rating} onRatingChange={setRating} />
                </div>
                <div className="form-group">
                    <label>Review:</label>
                    <textarea 
                        value={review} 
                        onChange={(e) => setReview(e.target.value)} 
                        rows="4" 
                        maxLength="1000"
                        placeholder="Write your review here (optional)"
                        style={{ width: '100%', padding: '5px', borderRadius: '4px', border: '1px solid #ccc' }}
                    ></textarea>
                </div>
                <button type="submit" style={{ padding: '10px 15px', border: 'none', backgroundColor: '#007bff', color: '#fff', borderRadius: '5px', cursor: 'pointer' }}>
                    Submit Review
                </button>
            </form>
        </div>
    );
};

export default ReviewForm;
