import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ReviewForm = ({ productId, onReviewSubmitted }) => {
    const [rating, setRating] = useState(0);
    const [review, setReview] = useState('');
    const [error, setError] = useState('');
    const [hasReviewed, setHasReviewed] = useState(false);

    useEffect(() => {
        checkExistingReview();
    }, [productId]);

    const getCurrentUser = () => {
        return {
            id: localStorage.getItem('auth_id'), // Adjust if your user ID key is different
            name: localStorage.getItem('auth_name'),
        };
    };

    const getAuthToken = () => {
        return localStorage.getItem('auth_token');
    };

    const checkExistingReview = async () => {
        try {
            const response = await axios.get(`/api/products/${productId}/reviews`);
            const currentUser = getCurrentUser();

            const userReview = response.data.reviews.find(r => r.user.id === currentUser.id);

            if (userReview) {
                setHasReviewed(true);
                setRating(userReview.rating);
                setReview(userReview.review);
            }
        } catch (error) {
            console.error('Error checking existing review:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        if (rating < 1 || rating > 5) {
            setError('Please select a rating between 1 and 5.');
            return;
        }

        try {
            const token = getAuthToken();

            const response = await axios.post('/api/reviews', {
                product_id: productId,
                rating,
                review,
            }, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                }
            });

            setHasReviewed(true);
            onReviewSubmitted(); // Callback to refresh reviews
        } catch (err) {
            if (err.response && err.response.data.message) {
                setError(err.response.data.message);
            } else {
                setError('An error occurred while submitting your review.');
            }
        }
    };

    if (hasReviewed) {
        return <p>You have already reviewed this product.</p>;
    }

    return (
        <div className="review-form">
            <h3>Submit Your Review</h3>
            {error && <p className="error">{error}</p>}
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Rating:</label>
                    <select value={rating} onChange={(e) => setRating(parseInt(e.target.value))}>
                        <option value="0">Select rating</option>
                        <option value="1">1 - Poor</option>
                        <option value="2">2 - Fair</option>
                        <option value="3">3 - Good</option>
                        <option value="4">4 - Very Good</option>
                        <option value="5">5 - Excellent</option>
                    </select>
                </div>
                <div className="form-group">
                    <label>Review:</label>
                    <textarea 
                        value={review} 
                        onChange={(e) => setReview(e.target.value)} 
                        rows="4" 
                        maxLength="1000"
                        placeholder="Write your review here (optional)"
                    ></textarea>
                </div>
                <button type="submit">Submit Review</button>
            </form>
        </div>
    );
};

export default ReviewForm;
