import React from 'react';
import { SliderData } from './SliderData';
import ImageSlider from './ImageSlider';
import Footer from './Footer';
import { Link } from 'react-router-dom';
import ChatBotButton from './ChatBotButton';  // Make sure this path is correct

const Home = () => {
  return (
    <>
      {/* Image Slider Section */}
      <ImageSlider slides={SliderData} />

      {/* Main Hero Section with Text and Get Started Button */}
      <div className="slider-text">
        <h1>RentTheTrend</h1>
        <h6>Clothes for all</h6>
        <Link to="/register" className="get-started-button">Get started â†’</Link>
      </div>

      {/* Footer Section */}
      <Footer />

      {/* Fixed Chatbot Button in Bottom-Right Corner */}
      <div
        style={{
          position: 'fixed',
          bottom: '20px',
          right: '20px',
          zIndex: 1000,  // Ensures it's on top of other elements but not intrusive
        }}
      >
        <div
          style={{
            width: '60px',  // Restrict the size of the button
            height: '60px',
            borderRadius: '50%',  // Circular button
            backgroundColor: '#007bff',  // Button color
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',  // Floating effect
            cursor: 'pointer'
          }}
        >
          <ChatBotButton />
        </div>
      </div>
    </>
  );
}

export default Home;
